CREATE DATABASE IF NOT EXISTS COMP2800;
        use COMP2800;
        CREATE TABLE IF NOT EXISTS BBY_8_user (
            ID int NOT NULL AUTO_INCREMENT,
            firstName varchar(30),
            lastName varchar(30),
            email varchar(30),
            password varchar(30),
            role char,
            userName varchar(30),
            age int,
            personality varchar(30),
            filesrc varchar (100),
            PRIMARY KEY (ID)
        );
        use COMP2800;
        CREATE TABLE IF NOT EXISTS BBY_8_survey (
            userID int NOT NULL,
            dateOfSurvey DATE,
            survey varchar(30),
            PRIMARY KEY (userID, dateOfSurvey),
            FOREIGN KEY (userID) REFERENCES BBY_8_user(ID) ON DELETE CASCADE
        );
        CREATE TABLE IF NOT EXISTS BBY_8_album (
            ID int NOT NULL AUTO_INCREMENT,
            title varchar(30), 
            artist varchar(30),
            genre varchar(30),
            dateofRelease DATE,
            filesrc varchar (100),
            PRIMARY KEY (ID)
        );
        CREATE TABLE IF NOT EXISTS BBY_8_song (
            ID int NOT NULL AUTO_INCREMENT,
            title varchar(50),
            artist varchar(80),
            personality varchar(4),
            mood varchar(30), 
            album varchar(80),
            youtubeLink varchar(70),
            spotifyLink varchar(80),
            filesrc varchar (100),            
            PRIMARY KEY (ID)
        );
        CREATE TABLE IF NOT EXISTS BBY_8_library (
            userID int NOT NULL,
            songID int NOT NULL,
            PRIMARY KEY (userID, songID),
            FOREIGN KEY (userID) REFERENCES BBY_8_user(ID) ON DELETE CASCADE,
            FOREIGN KEY (songID) REFERENCES BBY_8_song(ID) ON DELETE CASCADE
        );
        CREATE TABLE IF NOT EXISTS BBY_8_review (
            userID int NOT NULL,
            songID int NOT NULL,
            dateOfReview DATE,
            review varchar(150),
            PRIMARY KEY (userID, songID, dateOfReview),
            FOREIGN KEY (userID) REFERENCES BBY_8_user(ID) ON DELETE CASCADE,
            FOREIGN KEY (songID) REFERENCES BBY_8_song(ID) ON DELETE CASCADE
        );
        CREATE TABLE IF NOT EXISTS BBY_8_post (
			ID int NOT NULL AUTO_INCREMENT,
            userID int NOT NULL,
            dateOfPost DATETIME,
            post varchar(150),
            filesrc varchar (100),
            PRIMARY KEY (ID),
            FOREIGN KEY (userID) REFERENCES BBY_8_user(ID) ON DELETE CASCADE
        );
